import React, { Component } from "react";
import Constants from "../Constants";
import Reel from "./Reel";
import "../assets/css/App.css";

class ReelSet extends Component {
  constructor(props) {
    super(props);
    this.state = {
      rolling: false,
      width: null,
      height: null,
      bet: 0,
      score: 2,
    };
  }

  render() {
    const reelSet = {
      flex: 1,
      flexDirection: "row",
      backgroundColor: "green"
    };
    const container = {
      flex: 1,
      justifyContent: "center",
      alignItems: "center",
      backgroundColor: "purple"
    };
    const playContainer = {
      height: Constants.MAX_HEIGHT,
      width: Constants.MAX_WIDTH,
      backgroundColor: "black",
    };

    return (
      <div id="slot">
        <div id="jackpot">
          Jackpot: <span id="jp">100</span>
        </div>
        <div style={container}>
          <div style={playContainer}>
            <div style={reelSet}
              //   myScore={score}
             // onLayout={this.onLayout}
             
             >
               <div>
                 {this.state.width&&this.state.height&&this.renderReels()}
             </div>
            </div>
          </div>
        </div>
      <div id="controls">
  <div
    className={!this.state.rolling ? "roll rolling" : "roll"}
    onClick={() => {
      this.spin();
      // this.reelSet.spin();
      //    console.log("My reelset score is:"+this.reelSet.onScore());
  //  this.setMyScore(this.onScore());

      console.log("executing now");
    }}
    disabled={this.state.rolling}
  >
    {this.state.rolling ? "Rolling..." : "ROLL"}
  </div>
  <div
    className={!this.state.withdrawal ? "roll rolling" : "roll"}
    onClick={() => {}}
    disabled={this.state.withdrawal}
  >
    {this.state.withdrawal ? "Withdrawaling..." : "Withdrawal"}
  </div>
  <button
    id="setBet"
    onClick={this.increaseBet}
    disabled={this.state.disabled}
  >
    +
  </button>
  <div className={!this.state.bet ? "bet rolling" : "bet"}>
    Bet:{this.state.bet}
  </div>
  <button
    id="setBet"
    onClick={this.decreaseBet}
    disabled={this.state.disabled}
  >
    -
  </button>
  <label>
    Score:<span id="score">{this.state.score}</span>
  </label>
</div>
      </div>
    );
  }
}

export default ReelSet;
