import React, {useState} from 'react';
import Constants from '../Constants';
import Symbol from './Symbol';



const Reel = (props) =>{
    //42 symbols
    const symbols = "DDDGNNSSVVVCCFFRRYYIIJJZZMMOOLLTTPPBBQQAAG";
    const reelSymbols = symbols.repeat(Constants.REELS_REPEAT).split("");
//    const [reelEffect,setReelEffect] = useState(0);

    console.log("My math random is: "+ (Math.floor((Math.random() * 42)+1)));
    console.log("My props.st: "+props.st);

    let reelEffect =0;

    if(props.st+props.idx<42){
        reelEffect= -(props.st+props.idx)*242
    }else{
        reelEffect =-(props.st*242);
    }

   

    console.log("My reelEffect is : "+reelEffect);

    const renderImages = () =>{
        let imagesList = reelSymbols.map((el, idx)=>
            {return <Symbol symbol={el} key={idx} index={idx} spin={reelEffect} />
         })
         reelEffect=0;
         return <>{imagesList}</>
        }

    return(
        
        <div className="reel" >
            {renderImages()}
        </div>
    )
}
export default Reel;
