import React, { useState, useRef, useEffect, forwardRef } from "react";
import Constants from "../Constants";
import Symbol from "./Symbol";
import {motion,useAnimation, variants} from 'framer-motion';
import '../assets/css/App.css';

let opacity =1;

const Reel = forwardRef((props, ref) => {
  //42 symbols
//  const symbols = "DDDGNNSSVVVCCFFRRYYIIJJZZMMOOLLTTPPKKQQAAG";
//const symbols = "DDDGNNSS";
  let reelRef= useRef();
  const symbols = Constants.REEL_SYMBOLS;
  let reelSymbols = "";
  let symbolsRefs = [];
  const controls = useAnimation()
  let currentScrollPos = 0;
  if(props.opacity <1){
    console.log("My opacity from Reel is:"+props.opacity)
    opacity=props.opacity;
  }

  if(props.gameOn===0){
      reelSymbols=symbols[props.index].split("");
  }else{
    reelSymbols= symbols[props.index].repeat(Constants.REELS_REPEAT).split("");
  }  
// console.log("My number is: "+(-(props.gameOn/242)));
// console.log("Returning selected Symbol:"+reelSymbols[(-(props.gameOn/242))]);
// console.log("Returning selected Symbol:"+reelSymbols[(-(props.gameOn/242))+1]);
// console.log("Returning selected Symbol:"+reelSymbols[(-(props.gameOn/242))+2]);

// let getReels="";


useEffect (()=>{
  console.log("My symbolsRefs from Reel is: "+symbolsRefs[0]);
})

const variants =  {
    spin:{ 
      y: props.spin,
      opacity:1,
      transition: {
          duration:1,
        }
      
    },
    transition:{ transition: {
      delay: 0.1*Math.floor(props.index/3)
    }},
    hidden: {opacity: 0},
    duration: {duration:10}
}

const scrollByOffSet = (offSet) => {
    currentScrollPos = currentScrollPos + (-1 * Constants.REEL_WIDTH * offSet);    
    console.log("My currentScrollPos is:"+currentScrollPos);
}

const setupReel= ()=>{
  controls.start();
  return (
  <div className="reel">
    <div className="customReel">
        <div className="icons">
    {
    reelSymbols.map((el, idx) => {
    return  <Symbol symbol={el}  key={idx} index={idx} spin={props.gameOn} opacity={opacity} myArray={symbolsRefs[idx]=ref} />;
      })} 
      </div>
    </div>
  </div>
);
}

  return (
      <>{setupReel()}</>
    );
});
export default Reel;


