import React,{useState, useRef} from 'react'
import Withdrawal from './Withdrawal';
import ScoreBet from './ScoreBet'

const Controller = (props) =>{
    const [spinning, setSpinning] = useState(false);
    let bet=2;
    let score=2;
    let refScore = useRef();
    let btnDisable=false;

    const setBetCallBack=(myBet)=>{
        bet=myBet;
    }

    const setScoreCallBack = (myScore) =>{
        score=myScore;
    }
    
    const callbackSpin =()=>{
        if(bet===0){
          window.confirm("Please place a bet to play !");
        }else{
            
            if(!rolling){
              setRolling(true);
              props.spinReel();
             }
            setTimeout(()=>{
              setRolling(false);
          },10000)
        }
    }

    return(
        <div id="controls" ref={refScore}>
        <div
          className={!rolling ? "spin spinning" : "spin"}
          onClick={() => {
            callbackSpin();
          }}
          disabled={rolling} >
          {spinning ? "Spinning..." : "SPIN"}
        </div>
        
        <ScoreBet block={rolling} resultsArray={props.myResults} resultsArrayIdx={props.myResultsIdx} callBackSetBet={setBetCallBack} callbackSetScore={setScoreCallBack} game={props.game} winningLinesCallBack={props.winningLinesCallBack} looserCallBack={props.looserCallBack}/>    
      </div>
    )
}

export default Controller;
