import React, { useRef,useEffect, useState } from "react";
import {ethers} from "ethers";
import ReelSet from "./components/ReelSet";
import "./assets/css/App.css";
import Mint from "./components/Mint";
import PFPRacerNFT from './artifacts/contracts/PFPRacerNFT.sol/abi.json'
import { assert } from "chai";
import GameScoreCarousels from './components/GameScoreCarousel';
import WelcomeMessage from './components/WelcomeMessage';

import axios from 'axios';

const App = () => {
  const [spin, setSpin] = useState(0);
  const [showList, setShowList] = useState(0);
  const [widthCarousel, setWidthCarousel] = useState(0);
  const carousel = useRef();

  const [account, setAccount] = useState("");
  const [contract, setContract] = useState("");
  const contractAddr = "0xDFe7ff7386e0C4321Bd1fc7d5f47887C91C7455B";

  const initConnection = async () =>{
    if(typeof window.ethereum !== "undefined"){
      const accounts = await window.ethereum.request({
        method: "eth_requestAccounts",
      });
      const provider = new ethers.providers.Web3Provider(window.ethereum);
      const gasPrice = await provider.getGasPrice();
      const newSigner = provider.getSigner();
      setAccount(accounts[0]);
      setContract(
        new ethers.Contract(
          contractAddr,
          PFPRacerNFT.abi,
          newSigner
        )
      )
    }else{
      alert("Please install metamask");
    }
  }

  const callbackSetSpin = (spin) => {
    setSpin(spin);
  };

  const callBackShowWin = () => {
    setShowList(1);
  };

  useEffect(()=>{
    initConnection();
  },[])

  return (
    <>
      <WelcomeMessage/>
      <ReelSet
        runGame={spin}
        spinCallback={callbackSetSpin}
        showWinCallback={callBackShowWin}
      />
      <GameScoreCarousels/>
    </>

  );
};
export default App;
