import React, { useState, useRef, useEffect, forwardRef } from "react";
import Constants from "../Constants";
import Symbol from "./Symbol";

const Reel = (props) => {
  //42 symbols
//  const symbols = "DDDGNNSSVVVCCFFRRYYIIJJZZMMOOLLTTPPKKQQAAG";
//const symbols = "DDDGNNSS";
  const symbols = Constants.REEL_SYMBOLS;
  let reelSymbols = "";

  if(props.gameOn===0){
      reelSymbols=symbols[props.index].split("");
  }else{
    reelSymbols= symbols[props.index].repeat(Constants.REELS_REPEAT).split("");
  }  

  const createReels = () => {
    reelSymbols.map((el, idx) => {
      return <Symbol symbol={el} key={idx} index={idx} spin={props.gameOn} arg={props.arg} />;
  }) 

  }

  return (
    <div className="reel"  >
      {createReels}
    </div>);
};
export default Reel;


