import React, { useEffect, useRef, useState, forwardRef} from "react";
import Constants from "../Constants";
import Bet from "./Bet";
import Withdrawal from "./Withdrawal";
import "../assets/css/App.css";
import Reel from "./Reel";


const ReelSet = (props) => {

  const [rolling, setRolling] = useState(false);
  let reels=[];
  let reelsRef= useRef();
  const [score, setScore] = useState(2);

  const spin = () => {
    setScore(score+1);
  }

  const randomBetween = (min, max)=> {
    return Math.floor(Math.random()*(max-min+1)+min);
  }

  const renderReels = () => {
    let reelList = Array.apply(null,Array(Constants.REELS)).map((el,idx)=>{
      return <Reel key={idx} index={idx} symbol={el}  />
    })
    
    return <>{reelList}</>
  }

 const changeLights =() => {
  reelsRef.current.childNodes[0].children[0].children[0].children[0].attributes[2].nodeValue=" opacity: 1; transform: none;";
  reelsRef.current.childNodes[1].children[0].children[0].children[0].attributes[2].nodeValue=" opacity: 1; transform: none;";
  reelsRef.current.childNodes[3].children[0].children[0].children[2].attributes[2].nodeValue=" opacity: 1; transform: none;";
 }

  useEffect(()=>{
    console.log("My reels are: "+reelsRef.current);
    console.log("My reeels are: "+reelsRef.current.childNodes[0].children[0].children[0].children[0].attributes[2].nodeValue);
    changeLights();

//                    column                                 line          
//reelsRef.current.childNodes[4].children[0].children[0].children[2].attributes[2].nodeValue=" opacity: 1; transform: none;";    
  })

  return(
    <>
      <div id="jackpot">
        Jackpot: <span>100</span>
      </div>
      <div className="container">
        <div className="playContainer">
          <div className="reelSet" ref={reelsRef}>
          {renderReels()}{changeLights()}
          </div>
        </div>
      </div>
      <div id="controls">
      <div
          className={!rolling ? "roll rolling" : "roll"}
          onClick={() => {
            spin();
          }}
          disabled={rolling}
        >
          {rolling ? "Rolling..." : "ROLL"}
        </div>
      </div>
    </>
  )

}

export default ReelSet;
