import React, { useState, useRef, useEffect, forwardRef } from "react";
import Constants from "../Constants";
import Symbol from "./Symbol";
import { motion } from "framer-motion";

const Reel = (props) => {
  const symbols = Constants.REEL_SYMBOLS;
  let reelSymbols = "";

  let symbolRef = useRef();
  let reelRef = useRef([]);

  if (props.gameOn === 0) {
    reelSymbols = symbols[props.index].split("");
  } else {
    reelSymbols = symbols[props.index].repeat(Constants.REELS_REPEAT).split("");
  }

  const variants = {
    opacity: {
      duration: 3,
      opacity: 0.3,
    },
  };

  useEffect(() => {
    console.log("My refSymbol is: "+symbolRef.current.childNodes[0].attributes[2].nodeValue);
  });

  return (
    <div className="reel" >
      <motion.div className="icons" animate="opacity" variants={variants} ref={symbolRef}>
        {reelSymbols.map((el, idx) => {
          return (
            <Symbol symbol={el} key={idx} index={idx} spin={props.gameOn} />
          );
        })}
      </motion.div>
    </div>
  );
};
export default Reel;

