import React, { useState, useRef, useEffect, forwardRef } from "react";
import Constants from "../Constants";
import Symbol from "./Symbol";

const Reel = forwardRef ((props, ref)=> {
  //42 symbols
  const symbols = "DDDGNNSSVVVCCFFRRYYIIJJZZMMOOLLTTPPBBQQAAG";
  let symbolsRefs = useRef();
  let reelSymbols = "";

  if(props.gameOn===0){
      reelSymbols=symbols.split("");
  }else{
    reelSymbols= symbols.repeat(Constants.REELS_REPEAT).split("");
  }  

  useEffect(()=>{
      console.log("My imageRefSymbol is "+symbolsRefs.current);
  },[])

  return (
    <div className="reel" >{reelSymbols.map((el, idx) => {
        return <Symbol symbol={el} key={idx} index={idx} spin={props.gameOn}/>;
    })} 
    </div>);
});
export default Reel;
