import React,{ useState, useRef, useEffect } from "react";
import Constants from "../Constants";
import "../assets/css/App.css";
import Reel from './Reel'
import Controls from "./Controls";

const ReelSet =(props) => {

  const[width, setWidth] = useState();
  const[height, setHeight] = useState();

  const refHeighWidth = useRef();

  useEffect(()=>{
    setWidth(refHeighWidth.current.clientWidth);
    setHeight(refHeighWidth.current.clientHeight);
      console.log("My reelSet hight is:"+refHeighWidth.current.clientHeight);
      console.log("My reelSet width is:"+refHeighWidth.current.clientWidth);
    
  },[])

  const renderReels=()=>{
    return(
      <div>
          <p>Testing 123</p>
          <p>Testing 123</p>
          <p>Testing 123</p>
          <p>Testing 123</p>
          <p>Testing 123</p>
      </div>
    )
  }

  return(
    <div>
    <div id="jackpot">
      Jackpot: <span>100</span>
    </div>
    <div className="container">
      <div className="playContainer">
        <div ref={refHeighWidth} className="reelSet" width={width} height={height} >
            {width&&height&&renderReels()}
        </div>
      </div>
    </div>
    <Controls/>
    </div>
   
  )
}

export default ReelSet;
