import React, { useState, useRef, useEffect, useCallback } from "react";
import ReelSet from "./components/ReelSet"
import './assets/css/App.css'

import './assets/css/App.css';

const  App=(props)=> {

  const [score, setScore] = useState(2);
 // console.log("My score is: "+scr);
let myScore=2;

let plm = useRef();

useEffect(()=>{
  plm=score;
  console.log("My plm= "+myScore);
},[])

const handleCallback = (e)=>{
  console.log("Adding to score: "+e);
  myScore=Math.round((myScore+e) * 100) / 100;
  console.log("My final score is:"+myScore);
  console.log("My score is: "+score);
} 


  return (
    <>
      <ReelSet dadCallback={handleCallback} ref={plm}/>
      <div id="controls">
            <label>
              Score:<span id="score">{score}</span>
            </label>
          </div>
    </>
  );
}
export default App;
