import React, { forwardRef, useEffect, useRef, useState} from "react";
import Constants from "../Constants";
import Bet from "./Bet";
import Withdrawal from "./Withdrawal";
import "../assets/css/App.css";
import Reel from "./Reel";
import {motion} from 'framer-motion';

let score=2;
let bet=0;

const ReelSet = forwardRef((props, ref) => {
  const [rolling, setRolling] = useState(false);
  const [gameOn, setGameOn] = useState(0);

  const reel= useRef();
  const reels= useRef([]);

  const randomBetween = (min, max) => {
    return Math.floor(Math.random()*(max-min+1)-min);
  }

  useEffect(()=>{
    console.log("My useRef is" );
  },[])

  const renderReels=()=>{
    let reelList = Array.apply(null, Array(Constants.REELS)).map((el, idx) => {
      return <Reel reel={el} key={idx} index={idx} />;
    });
 
    return <>{reelList}</>;
  }

  const spinReel = (idx, reel) => {
    for(let i=0; i<Constants.REELS; i++){
      console.log("Creating reel");
    }

      setGameOn(gameOn + 1);
      
  }

  const callbackBet = () => {

  }

  const variants = {
    opacity: {
       opacity:0.3, 
       duration: 0.3,
       delay: randomBetween(500,800)
    }
  };

  return (
    <>
    <div id="jackpot" >
    Jackpot: <span>100</span>
  </div>
      <div className="container" >
        <div className="playContainer">
          <motion.div animate="opacity" variants={variants} className="reelSet" ref={reel}>
            {renderReels()} 
          </motion.div>
        </div>
      </div>
            <div id="controls">
            <div
              className={!rolling ? "roll rolling" : "roll"}
              onClick={() => {
                spinReel();
              }}
              disabled={rolling} >
              {rolling ? "Rolling..." : "ROLL"}
            </div>
            <Withdrawal/>
            <Bet score={score} setBet={callbackBet} />
            <label>
              Score:<span id="score">{score}</span>
            </label>
          </div>
          </>
  );
});

export default ReelSet;
