import React, { useEffect, useRef, forwardRef} from "react";
import Image from "../assets/images/Images";

import {motion,animate,Variants} from "framer-motion";

const Symbol = (props)=> {
  let image = "";
  let imageRef=  useRef();

  const getImage =  () => {
    if(props.spin<0){
    switch (props.symbol) {
      
      case "A":
        return Image.America;
      case "G":
        return Image.Aragon;
      case "N":
        return Image.Argentina;
      case "S":
        return Image.Assen;
      case "U":
        return Image.Australia;
      case "C":
        return Image.Catalunya;
      case "F":
        return Image.Finland;
      case "R":
        return Image.France;
      case "Y":
        return Image.Germany;
      case "I":
        return Image.Indonesia;
      case "J":
        return Image.Japan;
      case "Z":
        return Image.Jerez;
      case "M":
        return Image.Malaysia;
      case "O":
        return Image.Misano;
      case "L":
        return Image.Mugello;
      case "T":
        return Image.Osterreich;
      case "P":
        return Image.Portugal;
      case "Q":
        return Image.Qatar;
      case "H":
        return Image.Thailand;
      case "K":
        return Image.UK;
      case "D":
        return Image.Fool;
      case "V":
        return Image.Valencia;
      default:
        return Image.Fool;
    }
  }else{
    return Image.Fool;
  }
  };

    image = getImage();

    props.setImageReels(image);

    const variants =  {
        spin:{ 
          y: props.spin,
          opacity:1,
            transition: {
              duration:5
            }
          
        },
        transition:{ transition: {
          delay: 0.1*Math.floor(props.index/3)
        }},
        hidden: {opacity: 0},
        duration: {duration:10}
    }

    useEffect (()=>{
      //console.log("My image is: "+imageRef.current.currentSrc);
    });

    const spinReel =()=>{
      return (
          <motion.img key={props.index}   
                      variants={variants}
                      animate="spin"                      
                      src={image} alt="" ref={imageRef} />
      );
  }
  
    return (
      <>{spinReel()}</>
    );

}

export default Symbol;

