  },[])

  return (
    <>
      <div id="jackpot" >
        Jackpot: <span>100</span>
      </div>
      <ReelSet addScore={addScore} startGame={gameOn} />

      <div id="controls">
        <div
          className={!rolling ? "roll rolling" : "roll"}
          onClick={() => {
            handleSpin();
          }}
          disabled={rolling} >
