import React, { useState, useRef } from "react";
import Constants from "../Constants";
import Reel from "./Reel";
import '../assets/css/App.css'

const ReelSet = (props) => {
  const [width, setWidth] = useState();
  const [height, setHeight] = useState();
  const [bet, setBet] = useState(0);
  const [score, setScore] = useState(2);

  let reels = [];
  let reelsInMotion = null;
  let spinResults = [];
  let winningLines = [];
  let strike = false;
  const LINES = Constants.LINES;
  let scr = 2;
  let countWin = 0;
  let symbolSellection = [];

  const randomBetween = (min, max) => {
    return Math.floor(Math.random() * (max - min + 1) + min);
  };

  const audioUrlWin = require("../assets/sounds/coinsHandling.wav");
  const audioWin = Audio("../assets/sounds/coinsHandling.wav");

  const audioUrlWinBonus = require("../assets/sounds/winningBonus.wav");
  const audioWinBonus = Audio("../assets/sounds/winningBonus.wav");

  const audioUrlBtn = require("../assets/sounds/buttonPress.wav");
  const audioBtn = Audio("../assets/sounds/buttonPress.wav");

  const audioUrlSpin = require("../assets/sounds/spinningSlotMachine.wav");
  const audioSpin = Audio("../assets/sounds/spinningSlotMachine.wav");

  //    evaluateResults = () => {
  //        winningLines = [];
  //        console.log(spinResults);
  //    }

  const drawScore = (lineNumberType, fool) => {
    let newScore = 0;
    if (fool) {
      lineNumberType = 18;
    }
    switch (lineNumberType) {
      case 0: // top line
        //        audioWin.play();
        newScore = 0.5;
        break;
      case 1: // middle line
        //        audioWin.play();
        newScore = 0.5;
        break;
      case 2: // bottom line
        //        audioWin.play();
        newScore = 0.5;
        break;
      case 3: // V shape starting bottom left
        //        audioWin.play();
        newScore = 0.2;
        break;
      case 4: // V shape starting bottom right
        //        audioWin.play();
        newScore = 0.2;
        break;
      case 5: // W shape starting top left
        //        audioWin.play();
        newScore = 0.3;
        break;
      case 6: // M shape starting bottom left
        //        audioWin.play();
        newScore = 0.3;
        break;
      case 7: // M shape on top half
        //        audioWin.play();
        newScore = 0.3;
        break;
      case 8: // W shape on top half
        //        audioWin.play();
        newScore = 0.3;
        break;
      case 9: //W shape on bottom half
        //        audioWin.play();
        newScore = 0.3;
        break;
      case 10: // M shape on bottom half
        //        audioWin.play();
        newScore = 0.3;
        break;
      case 11: // U shape on top half
        //        audioWin.play();
        newScore = 0.1;
        break;
      case 12: // inverse U shape on top half
        //        audioWin.play();
        newScore = 0.1;
        break;
      case 13: // U shape on bottom half
        //        audioWin.play();
        newScore = 0.1;
        break;
      case 14: // inverse U shape on bottom half
        //        audioWin.play();
        newScore = 0.1;
        break;
      case 15: // inverse U shape on top half
        //        audioWin.play();
        newScore = 0.1;
        break;
      case 16: // Z shape from top left
        //        audioWin.play();
        newScore = 0.4;
        break;
      case 17: // Z shape from bottom left
        //        audioWin.play();
        newScore = 0.4;
        break;
      case 18: // Z shape from bottom left
        //        audioWinBonus.play();
        newScore = 100;
        break;
      default:
        newScore = 0;
    }

    console.log("My score is:" + newScore);
    setMyScore(newScore);
    return;
  };

  const setScoreState = () => {
    console.log("My scr is:" + scr);
    setScore(scr);
  };

  const checkLinesElements = (symbol) => {
    countWin++;
    let symNr = 0;
    let fool = 0;
    for (let i = 0; i < Constants.LINES.length; i++) {
      fool = 0;
      symNr = 0;
      for (let j = 0; j < Constants.LINES[i].length; j++) {
        let indexI = Constants.LINES[i][j][0];
        let indexJ = Constants.LINES[i][j][1];

        if (spinResults[indexI][indexJ] === "D") {
          fool++;
        }
        if (symbol === spinResults[indexI][indexJ]) {
          symNr++;
        }
      }
      if (fool + symNr === 5 && fool < 4) {
        winningLines.push(i);
      }
    }
    winningLines = uniq(winningLines);
  };

  const uniq = (a) => {
    return a.sort().filter(function (item, pos, ary) {
      return !pos || item !== ary[pos - 1];
    });
  };

  const checkElements = () => {
    let fool = 0;
    for (let k = 0; k < 3; k++) {
      fool = 0;
      for (let i = 0; i < spinResults.length; i++) {
        let symb = "";
        for (let j = k; j < spinResults[i].length; j++) {
          if (spinResults[i][j] !== "D") {
            symb = spinResults[i][j];
            j = spinResults[i].length;
          } else {
            fool++;
            j = spinResults[i].length;
          }
        }
        if (fool < 4 && symb !== "") {
          symbolSellection.push(symb);
        }
      }
    }
    for (let i = 0; i < symbolSellection.length; i++) {
      checkLinesElements(symbolSellection[i]);
    }
  };

  const evaluateResults = () => {
    checkFourFools();
    checkElements();
    highLightWinningLines(0);
    if (winningLines.length > 0) {
      console.log("We have winning line !!!");
      for (let i = 0; i < winningLines.length; i++) {
        drawScore(winningLines[i], false);
      }
    } else {
      console.log("We have no winning lines !!!");
      if (scr - bet >= 0.2) {
        scr = Math.round((scr - bet) * 100) / 100;
        setScoreState();
        if (scr < bet) {
          setBetZero();
        }
      } else if (scr - bet === 0) {
        setBetZero();
        scr = 0;
        setScoreState();
      }
    }
    //winningLines=[];
  };

  const checkFourFools = () => {
    for (let i = 0; i < Constants.LINES.length; i++) {
      let fool = 0;
      for (let j = 0; j < Constants.LINES[0].length; j++) {
        let indexI = Constants.LINES[i][j][0];
        let indexJ = Constants.LINES[i][j][1];

        if (spinResults[indexI][indexJ] === "D") {
          fool++;
        }
      }
      if (fool === 4) {
        winningLines.push(i);
        drawScore(i, false);
      }
    }
  };

  const checkFiveFools = () => {
    let fl = false;
    for (let i = 0; i < Constants.LINES.length; i++) {
      let fool = 0;
      for (let j = 0; j < Constants.LINES[0].length; j++) {
        let indexI = Constants.LINES[i][j][0];
        let indexJ = Constants.LINES[i][j][1];
        if (spinResults[indexI][indexJ] === "D") {
          fool++;
        }
      }
      if (fool === 5) {
        fl = true;
        winningLines.push(i);
        drawScore(i, true);
        highLightWinningLines(0);
        break;
      }
    }
    return fl;
  };

  const checkWin = () => {
    let fool = checkFiveFools();
    if (!fool) {
      evaluateResults();
    }
    //  setScoreState();
  };

  const highLightWinningLines = (currentIndex) => {
    if (!winningLines.length) {
      return;
    }
    if (currentIndex > 0) {
      //turn off the light on the previous line
      Constants.LINES[winningLines[currentIndex - 1]].map((el) => {
        return reels[el[0]].highLightAtIndex(el[1], false);
      });
    }
    if (currentIndex > winningLines.length - 1) {
      return;
    }

    Constants.LINES[winningLines[currentIndex]].map((el) => {
      return (
        reels[el[0]].highLightAtIndex(el[1], true),
        reels[el[0]].shakeAtIndex(el[1])
      );
    });
    setTimeout(() => {
      highLightWinningLines(currentIndex + 1);
    }, 800);
  };

  const spin = () => {
    winningLines = [];
    reelsInMotion = Constants.REELS;
    for (let i = 0; i < Constants.REELS; i++) {
      reels[i].scrollByOffSet(
        randomBetween(
          (Constants.REELS_REPEAT - 6) * reels[i].symbols.length,
          (Constants.REELS_REPEAT - 5) * reels[i].symbols.length
        ),
        (reelIndex, results) => {
          reelsInMotion -= 1;
          spinResults[reelIndex] = results;

          if (reelsInMotion === 0) {
            checkWin();
            console.log("We have finished rolling");
            //  setScoreState();
          }
        }
      );
    }
  };

  const onLayout = (e) => {
    setWidth(1200);
    setHeight(800);
  };

  const renderReels = () => {
    let reelWidth = width / Constants.REELS;
    console.log("My reelWidth is: " + reelWidth);
    console.log("My hight is: " + height);
    let reelList = Array.apply(null, Array(Constants.REELS)).map((el, idx) => {
      return (
        <Reel
          width={reelWidth}
          height={height}
          key={idx}
          index={idx}
          refs={(ref) => {
            reels[idx] = ref;
          }}
        />
      );
    });

    return <>{reelList}</>;
  };

  const setMyScore = (e) => {
    console.log("We are in setMyScore: " + e);
    let sc = 0;
    if (bet > 0 && e > 0) {
      console.log("Calculating:" + scr + "+(" + bet + "*" + e + ")");
      scr = scr + (e + bet * e);
      scr = Math.round(scr * 100) / 100;
    } else if (bet > 0 && e === 0) {
      scr = scr - (e + bet * e);
      console.log("Calculating:" + sc + "-(" + bet + "*" + e + ")");
      scr = Math.round(scr * 100) / 100;
    } else if (bet === 0 && e > 0) {
      console.log("Calculating: " + scr + " + " + e);
      scr = Math.round((scr + e) * 100) / 100;
    }

    //setBetZero();
    setScoreState(scr);
  };

  const setBetZero = () => {
    setBet(0);
  };

  // to trigger roolling and maintain state

  const setWithdrawal = (wdrl) => {
    console.log("We are withdrawling !!!");
  };

  const increaseBet = () => {
    if (bet < scr - 0.2 && scr !== 0) {
      console.log("Bet is set !");
      setBet(Math.round((bet + 0.2) * 100) / 100);
    } else setBet(scr);
  };

  const decreaseBet = () => {
    if (bet >= 0.2 && score !== 0) {
      setBet(Math.round((bet - 0.2) * 100) / 100);
    } else if (bet === scr && score !== 0) {
      setBet(Math.round((score - 0.2) * 100) / 100);
    }
  };

  // let score = onScore();
  //  console.log("My render score is: "+score);
  return (
    <>
      <div id="slot">
        <div id="jackpot">
          Jackpot: <span id="jp">100</span>
        </div>
        <div className="container">
          <div className="playContainer">
            <div className="reelSet">
              {this.state.width && this.state.height && this.renderReels()}
            </div>
          </div>
        </div>
        <div id="controls">
          <div
            className="rolling"
            onClick={() => {
              this.spin();
              // this.reelSet.spin();
              //    console.log("My reelset score is:"+this.reelSet.onScore());
              //  this.setMyScore(this.onScore());

              console.log("executing now");
            }}
          >
            {this.state.rolling ? "Rolling..." : "ROLL"}
          </div>
          <div
            className="withdrawal" 
            onClick={() => {}}
            disabled={this.state.withdrawal}
          >
            {this.state.withdrawal ? "Withdrawaling..." : "Withdrawal"}
          </div>
          <button
            id="setBet"
            onClick={increaseBet}
          >
            +
          </button>
          <div className={!bet ? "bet rolling" : "bet"}>
            Bet:{bet}
          </div>
          <button
            id="setBet"
            onClick={decreaseBet}
          >
            -
          </button>
          <label>
            Score:<span id="score">{score}</span>
          </label>
        </div>
      </div>
    </>
  );
};

export default ReelSet;
