      randomBetween(
        (Constants.REELS_REPEAT - 6) * this.reels[i].symbols.length,
        (Constants.REELS_REPEAT - 5) * this.reels[i].symbols.length
      ),
      (reelIndex, results) => {
        reelsInMotion -= 1;
