import React,{useState} from 'react';
import Reel from './Reel';


const ReelSet = async () => {

  const [gameOn, setGameOn] = useState(0);
  const [initialReelState, setInitialReelState] = useState([["D","D","D"],
                                                            ["D","D","D"],
                                                            ["D","D","D"],
                                                            ["D","D","D"],
                                                            ["D","D","D"],
                                                           ]);

 const retrieveStateImages = async ()=>{
  return await Promise.all (
     initialReelState.map((reel, indexReel)=>{
      return <Reel key={indexReel} index={indexReel} reels={reel} game={gameOn}/>
    }).then(console.log("We have retrieved all the objects"))
    
  )
 }

 return (
        <>{retrieveStateImages()}</>
 ) 

}
