import React, { useState, useRef, useEffect } from "react";
import Constants from "../Constants";
import "../assets/css/App.css";
import Reel from "./Reel";

const ReelSet = (props) => {
  //  const [spinReel, setSpinReel] = useState(false)
  let spinResults = [];
  let bet =0;
  let score=2;
  let scr=0;
  const winningLines = [];
  let reels= useRef();
  let countWin=0;
  let symbolSellection = [];
  let randomNr=0;

  const getRandomNr = () => {
    if (props.startGame > 0) {
      randomNr = -(Math.floor(Math.random() * 42) * 242);
    }
    return randomNr;
  };

  const showMyReels = () =>{
    for(let i=0; i<Constants.REELS; i++){
      for(let j=0; j<3;j++){
        console.log("My reel is: "+ (reels[i])[(-(randomNr/243))+j]);
      }
    }
  }
  
  const renderReels = () => {
      let reelList = Array.apply(null, Array(Constants.REELS)).map((el, idx) => {
      return <Reel reel={el} key={idx} index={idx} gameOn={getRandomNr()} ref={(ref) => {
       reels[idx] = ref.symbolsRefs}}/>;
    });
  //  spinReel();
    showMyReels();
    return <>{reelList}</>;
  };

  console.log("My spin results are "+ reels.length);
  
const drawScore = (lineNumberType, fool) => {
    let newScore = 0;
    if (fool) {
      lineNumberType = 18;
    }
    switch (lineNumberType) {
      case 0: // top line
//        audioWin.play();
        newScore = 0.5;
        break;
      case 1: // middle line
//        audioWin.play();
        newScore = 0.5;
        break;
      case 2: // bottom line
//        audioWin.play();
        newScore = 0.5;
        break;
      case 3: // V shape starting bottom left
//        audioWin.play();
        newScore = 0.2;
        break;
      case 4: // V shape starting bottom right
//        audioWin.play();
        newScore = 0.2;
        break;
      case 5: // W shape starting top left
//        audioWin.play();
        newScore = 0.3;
        break;
      case 6: // M shape starting bottom left
//        audioWin.play();
        newScore = 0.3;
        break;
      case 7: // M shape on top half
//        audioWin.play();
        newScore = 0.3;
        break;
      case 8: // W shape on top half
//        audioWin.play();
        newScore = 0.3;
        break;
      case 9: //W shape on bottom half
//        audioWin.play();
        newScore = 0.3;
        break;
      case 10: // M shape on bottom half
//        audioWin.play();
        newScore = 0.3;
        break;
      case 11: // U shape on top half
//        audioWin.play();
        newScore = 0.1;
        break;
      case 12: // inverse U shape on top half
//        audioWin.play();
        newScore = 0.1;
        break;
      case 13: // U shape on bottom half
//        audioWin.play();
        newScore = 0.1;
        break;
      case 14: // inverse U shape on bottom half
//        audioWin.play();
        newScore = 0.1;
        break;
      case 15: // inverse U shape on top half
//        audioWin.play();
        newScore = 0.1;
        break;
      case 16: // Z shape from top left
//        audioWin.play();
        newScore = 0.4;
        break;
      case 17: // Z shape from bottom left
//        audioWin.play();
        newScore = 0.4;
        break;
      case 18: // Z shape from bottom left
//        audioWinBonus.play();
        newScore = 100;
        break;
      default:
        newScore=0;
    }

    console.log("My score is:"+newScore);
    setScore(newScore);
    return;
  };

  const spinReel  = () =>{

    if(props.startGame>0){
      for (let i = 0; i < Constants.REELS; i++) {
      //  checkWin();
        console.log("We have finished rolling");
    }
  }  
}

const  setScoreState = () => {
    console.log("My scr is:"+scr);
    score=scr;
  };
  
const checkLinesElements=(symbol)=>{
    countWin++;
    let symNr=0;
    let fool=0;
    for(let i=0;i<Constants.LINES.length;i++){
        fool=0;
        symNr=0;
        for(let j=0; j<Constants.LINES[i].length;j++){
            let indexI = Constants.LINES[i][j][0];
            let indexJ = Constants.LINES[i][j][1];

            if(spinResults[indexI][indexJ]==='D'){
                fool++;
            }
            if(symbol===spinResults[indexI][indexJ]){
                symNr++;
            }
        }  
        if(fool+symNr===5&&fool<4){
            winningLines.push(i);
        }
    }
    winningLines=uniq(winningLines);
  }

const  uniq=(a)=> {
    return a.sort().filter(function(item, pos, ary) {
        return !pos || item !== ary[pos - 1];
    });
}


const checkElements=()=>{
      let fool=0;
    for(let k=0; k<3; k++){
      fool=0;
      for(let i=0;i<spinResults.length;i++){
          let symb ="";
          for(let j=k; j<spinResults[i].length;j++){
              if(spinResults[i][j]!=='D'){
                  symb=spinResults[i][j];
                  j=spinResults[i].length;
              }else{
                fool++;
                j=spinResults[i].length;
              }
          }
         if(fool<4&&symb!==""){
         symbolSellection.push(symb); 
        }
      } 
    } 
    for(let i=0;i<symbolSellection.length;i++){
        checkLinesElements(symbolSellection[i]);
    }
    
  }

const setBetZero = () => {
    bet=0;
  };

const  setScore = (e) => {
    console.log("We are in setScore: " + e);
    let sc = 0;
    if (bet > 0 && e > 0) {
      console.log("Calculating:" + scr + "+(" + bet + "*" + e + ")");
      scr = scr + (e+(bet * e));
      scr = Math.round(scr * 100) / 100;
    } else if (bet > 0 && e === 0) {
      scr= scr -(e+(bet * e));
      console.log("Calculating:" + sc + "-(" + bet + "*" + e + ")");
      scr = Math.round(scr * 100) / 100;
    } else if (bet === 0 && e > 0) {
      console.log("Calculating: " + scr + " + "+ e);
      scr= Math.round((scr+e) * 100)/100;
    }

    //setBetZero();
    setScoreState(scr);
  };
const checkFourFools = () => {
    for (let i = 0; i < Constants.LINES.length; i++) {
      let fool = 0;
      for (let j = 0; j < Constants.LINES[0].length; j++) {
        let indexI = Constants.LINES[i][j][0];
        let indexJ = Constants.LINES[i][j][1];
        
        if (spinResults[indexI][indexJ] === "D") {
                fool++;
        }
      }
      if (fool === 4) {
        winningLines.push(i);
        drawScore(i,false);
      } 
    }
  };

const evaluateResults = () => {
  checkFourFools();
  checkElements();
  //highLightWinningLines(0);
    if(winningLines.length>0){
      console.log("We have winning line !!!");
      for(let i=0; i< winningLines.length;i++){
          drawScore(winningLines[i],false);
    }
  }else{
    console.log("We have no winning lines !!!");
    if((scr - bet)>=0.2){
     scr= Math.round((scr - bet) * 100) / 100;
      setScoreState();
      if(scr < bet){
        setBetZero();
      }
    }else if((scr - bet)===0){
      setBetZero();
      scr=0;
      setScoreState();
    }
    
  }
    //winningLines=[];
  };
const checkFiveFools = () => {
    let fl = false;
    for (let i = 0; i < Constants.LINES.length; i++) {
      let fool = 0;
      for (let j = 0; j < Constants.LINES[0].length; j++) {
        let indexI = Constants.LINES[i][j][0];
        let indexJ = Constants.LINES[i][j][1];
        if (spinResults[indexI][indexJ] === "D") {
          fool++;
        }
      }
      if (fool === 5) {
        fl = true;
        winningLines.push(i);
      //  drawScore(i,true);
      //  highLightWinningLines(0);
        break;
      }
    }
    return fl;
  };

  const checkWin = () => {
    let fool = checkFiveFools();
    if (!fool) {
      evaluateResults();
    }
  //  setScoreState();
  };

  return (
    <>
      <div id="jackpot">
        Jackpot: <span>100</span>
      </div>
      <div className="container">
        <div className="playContainer">
          <div className="reelSet" >{
          renderReels()
          }</div>
        </div>
      </div>
    </>
  );
};

export default ReelSet;
