       let indexJ=(Constants.LINES[lineNr])[j][1];

      console.log("My highLightWinningLines winningLine is: "+(spinResultsIdx[indexI])[indexJ]);
      let imagePoz=(spinResultsIdx[indexI])[indexJ];
     
       refLights.current.childNodes[j].childNodes[0].childNodes[imagePoz].attributes[3].value="opacity: 1";
     
    } 
  },6000+(i*2000))
      // for(let j=0; j<Constants.LINES[lineNr].length;j++){   
      //   console.log("REEL NUMBER LightsOn"+ j);
      //    let indexI=(Constants.LINES[lineNr])[j][0];
      //    let indexJ=(Constants.LINES[lineNr])[j][1];
  
      //   let imagePoz=(spinResultsIdx[indexI])[indexJ];
       
        
      //   console.log("My highLightWinningLines winningLine is: "+(spinResultsIdx[indexI])[indexJ]);
      // } 
 
     
   
      
      console.log("End of Line: "+Constants.LINES[lineNr]);
  }
}


const callbackWinningResults =(winningLines) =>{
  winningResults=winningLines;
  highLightWinningLines();
}

  const renderReels = () => {
    let reelList = Array.apply(null, Array(Constants.REELS)).map((el, idx) => {
      return <Reel reel={el} key={idx} index={idx} gameOn={gameOn} createArray={callbackCreateArray}/>;
    });
    
    return <>{reelList}</>;
  };  

 const calbackZeroGame = ()=>{
  zeroGame=false;
 } 

  const callBackSpinReel = () => {
      setGameOn(gameOn+1);  
  }; 

  return (
    <>
    <div id="jackpot" >
    Jackpot: <span>100</span>
  </div>
      <div className="container" >
        <div className="playContainer">
          <div className="reelSet"  ref={refLights}>
            {renderReels()}
          </div>
        </div>
      </div>
      <Controller spinReel={callBackSpinReel} zeroGame={calbackZeroGame} myResults={spinResults} myResultsIdx={spinResultsIdx} game={gameOn} winningLinesCallBack={callbackWinningResults}/>
      </>
  );
};

export default ReelSet;

