import React, { useState, useRef, useEffect } from "react";
import Constants from "../Constants";
import Symbol from "./Symbol";

const Reel = (props) => {
  //42 symbols
  const symbols = "DDDGNNSSVVVCCFFRRYYIIJJZZMMOOLLTTPPBBQQAAG";
  const reelSymbols = symbols.repeat(Constants.REELS_REPEAT).split("");
  let [reel, setReel] = useRef();
  //    const [reelEffect,setReelEffect] = useState(0);
  console.log("My props.st: " + props.gameOn);
  const mySymbol = useRef();




  const renderImages = () => {
    let imagesList = reelSymbols.map((el, idx) => {
      return <Symbol symbol={el} key={idx} index={idx} spin={props.gameOn} mySymbol={el} stReel={setReel}/>;
    });

    
    return <>{imagesList}</>;
  };

  return <div className="reel">{renderImages()}</div>;
};
export default Reel;
