  const [gameOn, setGameOn] = useState(0);
  const [rolling, setRolling] = useState(false);


  const [score, setScore] = useState(2);

  const spin = () =>{
    return (
      <>
        <ReelSet startGame={true} game={gameOn}/>
      </>
      )
  }

  const handleSpin= ()=> {
    setGameOn(Math.floor(Math.random() * 42));
  }

  return (

    <>
      <div id="jackpot">
        Jackpot: <span>100</span>
      </div>
       {spin}
       <div id="controls">
  <div
    className={!rolling ? "roll rolling" : "roll"}
  onClick={() => {
    handleSpin();
    //  this.spin();
      // this.reelSet.spin();
      //    console.log("My reelset score is:"+this.reelSet.onScore());
  //  this.setMyScore(this.onScore());

      console.log("executing now");
    }}
    disabled={rolling}
  >
    {rolling ? "Rolling..." : "ROLL"}
  </div>
  <div
 //   className={!withdrawal ? "roll rolling" : "roll"}
    onClick={() => {}}
  //  disabled={withdrawal}
  >

  </div>
    <Bet score={score}/>
  <label>
    Score:<span id="score">2</span>
  </label>
  </div>
    </>
  );
}

export default App;
