import React, { useEffect, useRef, forwardRef} from "react";
import Image from "../assets/images/Images";
import {motion,animate,transform,variants} from "framer-motion";


const Symbol = forwardRef ((props, ref)=> {
  let image = "";
  let imageRef=  useRef();
  let symbOpacity=0.3;
 
  const getImage =  () => {
    if(props.spin<0){
    switch (props.symbol) {
      
      case "A":
        return Image.America;
      case "G":
        return Image.Aragon;
      case "N":
        return Image.Argentina;
      case "S":
        return Image.Assen;
      case "U":
        return Image.Australia;
      case "C":
        return Image.Catalunya;
      case "F":
        return Image.Finland;
      case "R":
        return Image.France;
      case "Y":
        return Image.Germany;
      case "I":
        return Image.Indonesia;
      case "J":
        return Image.Japan;
      case "Z":
        return Image.Jerez;
      case "M":
        return Image.Malaysia;
      case "O":
        return Image.Misano;
      case "L":
        return Image.Mugello;
      case "T":
        return Image.Osterreich;
      case "P":
        return Image.Portugal;
      case "Q":
        return Image.Qatar;
      case "H":
        return Image.Thailand;
      case "K":
        return Image.UK;
      case "D":
        return Image.Fool;
      case "V":
        return Image.Valencia;
      default:
        return Image.Fool;
    }
  }else{
    return Image.Fool;
  }
  };

    image = getImage();

    const setOpacity= ()=>{
     // return props.callbackSomethign(props.spin, props.symbol);

     if(props.Symbol==="V"){
      symbOpacity=0.3;
     }
     
    }


     
   const variants = {
    spin: {
      y: props.spin,
      transition:{
        opacity: setOpacity(),
        delay: 2,
        x: { stiffness: 50, damping:3 },
            default: { duration:0.5 }} 
        },
    hidden: { opacity: setOpacity(),
              transition:{
                delay: 4
              }
           }

   }

    return (
      <div className="icons" >
            <motion.img 
                      animate="spin"
                 //     initial="hidden"  
                      variants={variants}
                      src={image} alt="" value={image} 
                       />
        </div>
    );

});

export default Symbol;



