  const handleScore = (e) => {
    // /  const [gameScore, setGameScore] = useState({score:0, bet:0})
    score = Math.round((score + e) * 100) / 100;

    //  console.log("mySetGameScore = "+gameScore.score);
    console.log("My score=" + score);
    //setGameScore(score);
    // props.dadCallback(score)
  };
  const createArray = (randomNr, index) => {
    console.log(
      "My random nr is for list: " + symbols[index] + " => " + -(randomNr / 242)
    );
    for (let i = 0; i < 3; i++) {
      console.log("My sign is on position: " + (-(randomNr / 242) + i));
      results.push(symbols[index][-(randomNr / 242) + i]);
    }
    console.log("Results array has been created" + results);
  };

  const getRandomNr = (index) => {
    if (gameOn > 0) {
      randomNr = -(
        Math.floor(Math.random() * (symbols[index].length - 3) + 3) * 242
      );
      createArray(randomNr, index);
    }
    console.log("randomNr = " + randomNr);
    return randomNr;
  };

  const renderReels = (opacity) => {
    let reelList = Array.apply(null, Array(Constants.REELS)).map((el, idx) => {
      return <Reel reel={el} key={idx} index={idx} gameOn={getRandomNr(idx)} opacity={opacity} />;
    });
    randomNr = 0;
    console.log("Render Reels have finish Running: " + results);
    console.log("My renderReels bet is: " + bet);
    createResults();
    return <>{reelList}</>;
  };

  const drawScore = (lineNumberType, fool) => {
    let newScore = 0;
    if (fool) {
      lineNumberType = 18;
    }
    switch (lineNumberType) {
      case 0: // top line
        //        audioWin.play();
        newScore = 0.5;
        break;
      case 1: // middle line
        //        audioWin.play();
        newScore = 0.5;
        break;
      case 2: // bottom line
        //        audioWin.play();
        newScore = 0.5;
        break;
      case 3: // V shape starting bottom left
        //        audioWin.play();
        newScore = 0.2;
        break;
      case 4: // V shape starting bottom right
        //        audioWin.play();
        newScore = 0.2;
        break;
      case 5: // W shape starting top left
        //        audioWin.play();
        newScore = 0.3;
        break;
      case 6: // M shape starting bottom left
        //        audioWin.play();
        newScore = 0.3;
        break;
      case 7: // M shape on top half
        //        audioWin.play();
        newScore = 0.3;
        break;
      case 8: // W shape on top half
        //        audioWin.play();
        newScore = 0.3;
        break;
      case 9: //W shape on bottom half
        //        audioWin.play();
        newScore = 0.3;
        break;
      case 10: // M shape on bottom half
        //        audioWin.play();
        newScore = 0.3;
        break;
      case 11: // U shape on top half
        //        audioWin.play();
        newScore = 0.1;
        break;
      case 12: // inverse U shape on top half
        //        audioWin.play();
        newScore = 0.1;
        break;
      case 13: // U shape on bottom half
        //        audioWin.play();
        newScore = 0.1;
        break;
      case 14: // inverse U shape on bottom half
        //        audioWin.play();
        newScore = 0.1;
        break;
      case 15: // inverse U shape on top half
        //        audioWin.play();
        newScore = 0.1;
        break;
      case 16: // Z shape from top left
        //        audioWin.play();
        newScore = 0.4;
        break;
      case 17: // Z shape from bottom left
        //        audioWin.play();
        newScore = 0.4;
        break;
      case 18: // Z shape from bottom left
        //        audioWinBonus.play();
        newScore = 100;
        break;
      default:
        newScore = 0;
    }

    console.log("My Adding to score:" + score + " + " + newScore);
    if (gameOn > 0) {
      setMyScore(newScore);
    }
    return;
  };

  const createResults = () => {
    console.log("Calling createResults");
    let myResults = [];
    let j = 0;

    for (let i = 0; i <= results.length; i++) {
      myResults[j] = results[i];
      j++;

      if (j === 3) {
        spinResults.push(myResults);
        myResults = [];
        j = 0;
      }
    }

    console.log("spinResults =  " + spinResults);
    checkWin();
    // score=score+scr;
    // handleScore();
    // setScore(scr);
  };

  const spinReel = () => {
    console.log("My testBet value is" + bet);
    if (bet === 0 && score > 0) {
      alert("Please set a bet !!!");
    } else if (bet === 0 && score === 0) {
      alert("Game Over !!! You are you are out of cash !!!");
    } else {
      setGameOn(gameOn + 1);
      opc=0.3;

      console.log("GameOn is" + gameOn);
      //  checkWin();
      //  game=1+gameOn;
      // /  console.log("My spinReel game="+game);

      // props.parrentCallBack(score);
    }
  };

  useEffect(() => {
    console.log("useEffect bet is" + bet);
  }, [gameOn]);

  const callbackBet = (e) => {
    //bet=e;
    //setBet(()=>({bet:e}));

    bet = e;
    console.log("My bet callbackBet is:" + bet);
  };

