    countWin++;
    let symNr = 0;
    let fool = 0;
    for (let i = 0; i < Constants.LINES.length; i++) {
      fool = 0;
      symNr = 0;
      for (let j = 0; j < Constants.LINES[i].length; j++) {
        let indexI = Constants.LINES[i][j][0];
        let indexJ = Constants.LINES[i][j][1];

        if (spinResults[indexI][indexJ] === "D") {
          fool++;
        }
        if (symbol === spinResults[indexI][indexJ]) {
          symNr++;
        }
      }
      if (fool + symNr === 5 && fool < 4) {
        winningLines.push(i);
      }
    }
    winningLines = uniq(winningLines);
  };

  const uniq = (a) => {
    return a.sort().filter(function (item, pos, ary) {
      return !pos || item !== ary[pos - 1];
    });
  };

  const checkElements = () => {
    let fool = 0;
    for (let k = 0; k < 3; k++) {
      fool = 0;
      for (let i = 0; i < spinResults.length; i++) {
        let symb = "";
        for (let j = k; j < spinResults[i].length; j++) {
          if (spinResults[i][j] !== "D") {
            symb = spinResults[i][j];
            j = spinResults[i].length;
          } else {
            fool++;
            j = spinResults[i].length;
          }
        }
        if (fool < 4 && symb !== "") {
          symbolSellection.push(symb);
        }
      }
    }
    for (let i = 0; i < symbolSellection.length; i++) {
      checkLinesElements(symbolSellection[i]);
    }
  };

  const setBetZero = () => {
    bet = 0;
  };

  const setMyScore = (e) => {
    let scr = 0;

    if (bet > 0 && e > 0) {
      console.log(
        "Calculating 1: gameOn =" +
          gameOn +
          " | " +
          scr +
          "+(" +
          bet +
          "*" +
          e +
          ")"
      );
      scr = scr + (e + bet * e);
      scr = Math.round(scr * 100) / 100;
    } else if (bet > 0 && e === 0) {
      scr = scr - (e + bet * e);
      console.log(
        "Calculating 2: gameOn =" +
          gameOn +
          " | " +
          scr +
          "-(" +
          bet +
          "*" +
          e +
          ")"
      );
      scr = Math.round(scr * 100) / 100;
    } else if (bet === 0 && e > 0) {
      console.log("Calculating 3: gameOn =" + gameOn + " | " + scr + " + " + e);
      scr = Math.round((scr + e) * 100) / 100;
    }
    console.log("your setMyScore scr=" + scr);
    // score= Math.round((score+scr) * 100) / 100;

    console.log(
      "We are in setScore: " +
        e +
        " ,gameOn=" +
        gameOn +
        " ,score=" +
        score +
        " bet=" +
        bet
    );
    handleScore(scr);
    //  setScore(scr);
  };

  const checkFourFools = () => {
    for (let i = 0; i < Constants.LINES.length; i++) {
      let fool = 0;
      for (let j = 0; j < Constants.LINES[0].length; j++) {
        let indexI = Constants.LINES[i][j][0];
        let indexJ = Constants.LINES[i][j][1];
        //        console.log("Spin results from checkFourFools is: "+spinResults);
        //        console.log("checkFourFools indexI= "+indexI+"| indexJ= "+indexJ);
        console.log(
          "checkFourFools Comparing: " + spinResults[indexI][indexJ] + " === D"
        );
        if (spinResults[indexI][indexJ] === "D") {
          fool++;
        }
      }
      console.log("My fool from checkFourFools is: " + fool);
      if (fool === 4) {
        console.log("Pushing line nr.: " + i);
        winningLines.push(i);
        drawScore(i, false);
      }
    }
  };

  const evaluateResults = () => {
    checkFourFools();
    checkElements();
    //highLightWinningLines(0);
    if (winningLines.length > 0) {
      console.log("We have winning line !!!");
      for (let i = 0; i < winningLines.length; i++) {
        drawScore(winningLines[i], false);
      }
    } else {
      console.log("We have no winning lines !!!");
      if (score - bet >= 0.2) {
        score = Math.round((score - bet) * 100) / 100;
        //  setScore(scr);
        //handleScore(scr);
        console.log("your evaluateResults score - bet >= 0.2 => scr=" + score);
        if (score < bet) {
          setBetZero();
        }
      } else if (score - bet === 0) {
        setBetZero();
        score = 0;
        console.log("your evaluateResults scr=bet === 0 = > scr=" + score);
        //  setScore(scr);
        //handleScore(scr);
      }
    }
    //winningLines=[];
  };
  const checkFiveFools = () => {
    let fl = false;
    console.log("My spinResults checkFiveFools are: " + spinResults);
    for (let i = 0; i < Constants.LINES.length; i++) {
      let fool = 0;
      for (let j = 0; j < Constants.LINES[0].length; j++) {
        let indexI = Constants.LINES[i][j][0];
        let indexJ = Constants.LINES[i][j][1];
        console.log(
          "checkFiveFools comparing: " + spinResults[indexI][indexJ] + " === D"
        );
        if (spinResults[indexI][indexJ] === "D") {
          fool++;
        }
      }
      console.log("Checking 5 fools =" + fool);
      if (fool === 5) {
        fl = true;
        winningLines.push(i);
        drawScore(i, true);
        //  highLightWinningLines(0);
        break;
      }
    }
    console.log("We have checked 5 fools, and returning: " + fl);
    return fl;
  };

  const checkWin = () => {
    console.log("My checkWin is: " + gameOn + "My reels is:" + gameOn);
    if (gameOn > 0) {
      let fool = checkFiveFools();
      if (!fool) {
        evaluateResults();
      }
    }

    console.log("We are in checkWin: " + gameOn + "Final score is:" + score);
  };

  return (
    <>
      <div id="jackpot">
        Jackpot: <span>100</span>
      </div>
      <div className="container">
        <div className="playContainer">
          <div className="reelSet">{renderReels(opc)}</div>
        </div>
      </div>
      <div id="controls">
        <div
          className={!rolling ? "roll rolling" : "roll"}
          onClick={() => {
            spinReel();
          }}
          disabled={rolling}
        >
          {rolling ? "Rolling..." : "ROLL"}
        </div>
        <Withdrawal />
        <Bet score={score} setBet={callbackBet} />
        <label>
          Score:<span id="score">{score}</span>
        </label>
      </div>
    </>
  );
};

export default ReelSet;
