import React, {useState} from 'react';
import '../assets/css/App.css';
require('dotenv').config({path: "../.env"});


const Withdrawal=()=>{
    const [rolling, setRolling] = useState(false);
    
    const withdrawal = () => {
        console.log("Withdrawal has been activated");
      };
    return(
    <div
         className={withdrawal ? "roll rolling" : "roll"}
         onClick={() => {withdrawal()}}
    >Withdrawal</div>
    )
}

export default Withdrawal;
