import React, { useState, useRef, useEffect, forwardRef } from "react";
import Constants from "../Constants";
import Symbol from "./Symbol";
import { motion } from "framer-motion";

const Reel = (props) => {
  const symbols = Constants.REEL_SYMBOLS;
  let reelSymbols = "";
  let reels = [];

  let symbolRef = useRef();
  let reelRef = useRef([]);


  if (props.gameOn === 0) {
    reelSymbols = symbols[props.index].split("");
  } else {
    reelSymbols = symbols[props.index].repeat(Constants.REELS_REPEAT).split("");
  }

  let position = reelSymbols.length - Constants.SYMBOLS;
  let currentScrollPos= position * Constants.SYMBOL_HEIGHT * -1;

  useEffect(() => {
    console.log("My refSymbol is: "+symbolRef.current.childNodes[0].attributes[2].nodeValue);
  });

  const createReels = () =>{
    reels=reelSymbols.map((el, idx) => {
        return (
          <Symbol symbol={el} key={idx} index={idx} spin={props.gameOn} />
        );
      })
      return <>{reels}</>
  }

  const variants = {
    opacity: {
       y:currentScrollPos,
       opacity:1, 
       duration: 0.3,
       delay: 100*props.index
    }
  };

  return (
    <div className="reel" >
      <motion.div animate="opacity" variants={variants} className="icons" ref={symbolRef}>
        {createReels()}
      </motion.div>
    </div>
  );
};
export default Reel;

