import React, { useState, useRef } from "react";
import ReelSet from "./components/ReelSet";
import Bet from "./components/Bet";
import Withdrawal from "./components/Withdrawal";
import './assets/css/App.css';
import { getSelectionRange } from "@testing-library/user-event/dist/utils";
import { filterProps } from "framer-motion";

function App(props) {
  const [gameOn, setGameOn] = useState(0);
  const [rolling, setRolling] = useState(false);
  const [score, setScore] = useState(2);
  const [reelSet, setReelSet] = useState();

  let reels = [];
  let scr = useRef();

  const handleSpin = () => {
    setGameOn(gameOn+1);
    console.log("My state is : "+ gameOn);
  }

  console.log("My score is: "+scr);
  return (
    <>
      <div id="jackpot">
        Jackpot: <span>100</span>
      </div>
      <ReelSet startGame={gameOn} ref={score} />
      <div id="controls">
        <div
          className={!rolling ? "roll rolling" : "roll"}
          onClick={() => {
            handleSpin();
          }}
          disabled={rolling} >
          {rolling ? "Rolling..." : "ROLL"}
        </div>
        <Withdrawal/>
        <Bet score={score} />
        <label>
          Score:<span id="score">{}</span>
        </label>
      </div>
    </>
  );
}

export default App;
