    let reelList = Array.apply(null, Array(Constants.REELS)).map((el, idx) => {
      return <Reel reel={el} key={idx} index={idx} game={gameOn} opacity={1} createArray={callbackCreateArray}/>});
   
      console.log("My reelList size is:"+reelList.length);
