import React, { useState, useRef, useEffect } from "react";
import Constants from "../Constants";
import "../assets/css/App.css";
import Reel from "./Reel";

const ReelSet = ({nf}) => {

//  const [spinReel, setSpinReel] = useState(false)
const [rolling, setRolling] = useState(false);
const [bet, setBet] = useState(0);
const [score, setScore] = useState (2);

  const renderReels = function() {
    console.log("We are spinning the wheel !!!");
    let reelList = Array.apply(null, Array(Constants.REELS)).map((el, idx) => {
      console.log("Returnin reels");
      return <Reel key={idx} index={idx} start={true}/>;
    });

    console.log("My reelList is: " + reelList.length);
    return <>{reelList}</>;
  };

  const spin =() =>{
    
  }

  const increaseBet = () => {
    if(bet>=0&&score>=0.2){
      setBet( Math.round((bet+0.2)*100)/100);
    }
  }

  const decreaseBet = () => {
    if(bet>0){
      setBet( Math.round((bet-0.2)*100)/100);
    }
  }

  const withdrawal=()=>{
    console.log("Withdrawal class !");
  }

 
  return (
    <>
      <div id="jackpot">
        Jackpot: <span>100</span>
      </div>
      <div className="container">
        <div className="playContainer">
          <div
            className="reelSet" 
          >
            {renderReels()}
          </div>
        </div>
      </div>
      <div id="controls">
  <div
    className={!rolling ? "roll rolling" : "roll"}
    onClick={() => {
    //  this.spin();
      // this.reelSet.spin();
      //    console.log("My reelset score is:"+this.reelSet.onScore());
  //  this.setMyScore(this.onScore());

      console.log("executing now");
    }}
    disabled={rolling}
  >
    {rolling ? "Rolling..." : "ROLL"}
  </div>
  <div
    className={!withdrawal ? "roll rolling" : "roll"}
    onClick={() => {}}
  //  disabled={withdrawal}
  >
    {withdrawal ? "Withdrawaling..." : "Withdrawal"}
  </div>
  <button
    id="setBet"
    onClick={increaseBet}
  //  disabled={state.disabled}
  >
    +
  </button>
  <div className={!bet ? "bet rolling" : "bet"}>
    Bet:{bet}
  </div>
  <button
    id="setBet"
    onClick={decreaseBet}
  //  disabled={this.state.disabled}
  >
    -
  </button>
  <label>
    Score:<span id="score">2</span>
  </label>
  </div>
    </>
  );
};

export default ReelSet;
