import React from 'react'
import Reel from './Reel'
import '../assets/css/App.css'

const ReelSet =()=>{

const primaryState = [["D","D","D"],
                      ["D","D","D"],
                      ["D","D","D"],
                      ["D","D","D"],
                      ["D","D","D"],
                    ]

const renderState =()=>{
  let reelsList =primaryState.map((el, idx) => {
    return <Reel reel={el} key={idx} index={idx}  />;
  });

  return <>reelsList</>
}                  

  return(
  <>
    <div id="jackpot">
        Jackpot: <span>100</span>
    </div>
    <div className="container">
        <div className="playContainer">
          <div className="reelSet">
            {renderState}
            </div>
        </div>
      </div>
  </>
  )
}

export default ReelSet;
