import ReelSet from "./components/ReelSet"
import './assets/css/App.css'

import './assets/css/App.css';

const  App=(props)=> {

  const [score, setScore] = useState(2);
 // console.log("My score is: "+scr);
//let score=2;


const handleCallback = (e)=>{
  console.log("Adding to score: "+e);
  setScore(Math.round((score+e) * 100) / 100);
  console.log("My final score is:"+score);
} 

let gameScore=2;

  return (
    <>
      <ReelSet gameScore={gameScore} dadCallback={handleCallback}/>
      <div id="controls">
            <label>
              Score:<span id="score">{score}</span>
            </label>
          </div>
    </>
  );
}
export default App;
