import React, { useEffect, useRef, useState, forwardRef} from "react";
import Constants from "../Constants";
import Bet from "./Bet";
import Withdrawal from "./Withdrawal";
import "../assets/css/App.css";
import Reel from "./Reel";


const ReelSet = (props,ref) => {

  let reels=[];

  const randomBetween = (min, max)=> {
    return Math.floor(Math.random()*(max-min+1)+min);
  }

  const renderReels = () => {
    let reelList = Array.apply(null,Array(Constants.REELS)).map((el,idx)=>{
      return <Reel key={idx} index={idx} symbol={el} ref={reels[idx]=ref} />
    })

    return <>{reelList}</>
  }

  return(
    <>
      <div id="jackpot">
        Jackpot: <span>100</span>
      </div>
      <div className="container">
        <div className="playContainer">
          <div className="reelSet">
          {renderReels()}
          </div>
        </div>
      </div>
    
    </>
  )

};

export default ReelSet;
