import React, { useState,useEffect } from "react";
import Bet from './Bet';
import CalculateScore from './CalculateScore';

let bet=0;

const ScoreBet = (props) => {

  const [score, setScore] = useState(2);
  
  const callbackBet = (e) => {   
    console.log("CallBackBet ="+e); 
      bet=e;
      props.callBackSetBet(e);
  }

  const callbackScore = (scr) => {
    console.log("We set ScoreBet score to:"+scr);
    setTimeout(()=>{
     setScore(scr);
   },350)
 
  }



console.log("My bet inside ScoreBet is: "+bet);
console.log("My score inside ScoreBet is: "+score);

  return (
    <>
      <Bet score={score} setBet={callbackBet} />
      <CalculateScore spinResults={props.resultsArray} spinResultsIdx={props.resultsArrayIdx} myBet={bet} setCallbackScore={callbackScore} game={props.game}/>
    </>
  );
};
export default ScoreBet;
