import React, { forwardRef, useEffect, useRef, useState} from "react";
import Constants from "../Constants";
import Bet from "./Bet";
import Withdrawal from "./Withdrawal";
import "../assets/css/App.css";
import Reel from "./Reel";
import {motion} from 'framer-motion';

let score=2;
let bet=0;

const ReelSet = forwardRef((props, ref) => {
  const [rolling, setRolling] = useState(false);
  const [gameOn, setGameOn] = useState(0);

  const reel= useRef();
  const reels= [];

  const randomBetween = (min, max) => {
    return Math.floor(Math.random()*(max-min+1)-min);
  }


  useEffect(()=>{
    console.log("My reels are: "+reel.current.childNodes[0].children );

  },[score])

  const renderReels=()=>{
    return Array.apply(null, Array(Constants.REELS)).map((el, idx) => {
      return <Reel reel={el} key={idx} index={idx} game={gameOn} opacity={1} createArray={callbackCreateArray}/>});
  }

  const spinReel = () => {
    let reelList=[];
    if(gameOn===0){
      reelList= renderReels();
    }else if(gameOn>0){
      reelList=renderReels();
    }
    return <>{reelList}</>
  }

  const callbackBet = () => {

  }

  const callbackCreateArray = (reel) => {
    console.log("Receiving callback Array: "+reel);
    reels.push(reel);
  }



  return (
    <>
    <motion.div id="jackpot" >
    Jackpot: <span>100</span>
  </motion.div>
      <motion.div className="container" >
        <motion.div className="playContainer">
          <motion.div  className="reelSet" ref={reel}>
            {spinReel()}
          </motion.div>
        </motion.div>
      </motion.div>
            <div id="controls">
            <div
              className={!rolling ? "roll rolling" : "roll"}
              onClick={() => {
                spinReel();
              }}
              disabled={rolling} >
              {rolling ? "Rolling..." : "ROLL"}
            </div>
            <Withdrawal/>
            <Bet score={score} setBet={callbackBet} />
            <label>
              Score:<span id="score">{score}</span>
            </label>
          </div>
          </>
  );
});

export default ReelSet;
