import  React, { useEffect, useState, useRef } from "react";
import '../assets/css/App.css';
import {ethers} from "ethers";
import PFPRacerNFT from '../artifacts/contracts/PFPRacerNFT.sol/abi.json';
import { wait } from "@testing-library/user-event/dist/utils";
import axios from 'axios';
const cors = require("cors");

const Mint = (props) =>{
    const [mintAmount, setMintAmount] = useState(0);
    const [leftToMint, setLeftToMint] = useState(0);
    const url="https://vast-ridge-99820.herokuapp.com";

    const howManyNFTsAreLeft = async () =>{
      let totalNFT="";
      let mintedNFT="";
      
      totalNFT = await props.contract.getMaxSupply();//to be changed into getNFTBalance
      mintedNFT= await props.contract.getNFTBalance(); 
      
        console.log("My totalNFT's in howManyNFTsAreLeft is : "+totalNFT);
        console.log("My total mintedNFT is"+mintedNFT);
        let nftLeftToMint = totalNFT - mintedNFT;
        setLeftToMint(nftLeftToMint);
        console.log("Total NFT's left to mint "+nftLeftToMint);
      }

      const insertWalletIntoDB = async()=>{

        const dataToSend = JSON.stringify({ "wallet": props.account,
                                            "txtype":"mint",
                                            "amount":mintAmount,
                                             });
       
       console.log("My insertwalletintodb dataToSend is: "+dataToSend);
          const link = url+"/api/insertWallet";
          await fetch(link,{
      //  await fetch('http://localhost:3333/api/insertWallet', {
          method: 'POST',
          headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json',
          },
          body: dataToSend
        })
    
      }

const handleSubmit= async() =>{
  let transaction = "";
  let errorTx= false;
    if(mintAmount>0&&mintAmount<1001){
        try{
            const{ethereum}=window;

            if(ethereum){
   
                let cost = await props.contract.cost();
                cost = cost*mintAmount;
                console.log("Price is: "+cost);
              try{
                transaction = await props.contract.mint(mintAmount, {value: cost});
              }catch(err){
                errorTx=true;
                alert("Transaction failed, please try again later ! Transaction has been reverted ! ");
              }
              if(!errorTx){
                alert("Minted !! You can see transaction here:" +"<a href="+`https://mumbai.polygonscan.com/tx/${transaction.hash}`+">Transaction</a>");
            
                // await axios.get(`https://mumbai.polygonscan.com/tx/${transaction.hash}`)
                // .then(resp => {
                //     response=resp.data;
                //     console.log("My resonse data in get is: "+response);
                // });
                insertWalletIntoDB();
                await howManyNFTsAreLeft();
                setMintAmount(0);
              }
             } else{
                console.log("Ethereum object does not exist !");
             }   
        }
        catch(err){
            console.log("This is myerror"+err);
        }
     

    }else{
      if(mintAmount===0){
        alert("You need to mint at least 1 NFT");
      }else if(mintAmount >1000){
        alert("Holly Molly what do you wanna do ? Hold your horses and leave some for the others too!!! ");
      }else{
        alert("Number only !!!")
      }
    }
//    let nftTx = await contract.mint(mintAmount);
//    console.log("We are in handleSubmit: "+mintAmount);
    //mintAmount.preventDefault();
//,{ gasPrice: 33e9 }
  //  let gasPrice = await props.contract.getGasPrice();
    
   // console.log("Gas price is: "+gasPrice);
//   /  let nftTx = await contract.mint(mintAmount);
 //   console.log('Mining....', nftTx.hash)
 //   let tx= nftTx.wait();
  //  let nftTx = await props.contract.mint(mintAmount);
  //  let tx = nftTx.wait();
 //   console.log("Mined!", tx);
   // console.log("Mined!", tx);
   //await contract.withdrawal(mintAmount, "dbabe1135898ac301cfb00e6ce2122515ccae2390fc4c304eef3523a8f69843b");
    // howManyNFTsAreLeft();
  //  alert(`We have minted ${mintAmount}`)
    
}

    useEffect(()=>{
        howManyNFTsAreLeft();
    },[mintAmount])

      return (
        <>
        <div className="div-form">
        <form className="form">
        <label className="label-mint">
             NFT's left to mint:
             <p className="p-welcomeMint">{leftToMint}</p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
        </label>
          <label className="form-label">
            Mint Amount:
            <input
              type="text"
              value={mintAmount}
              onChange={e => setMintAmount(e.target.value)}
            />
          </label>
          <label className="label-mint">
            Price per/NFT is 10 Matic
            </label>
        </form>
        </div>
     <button className="form-submit-button" onClick={()=>{handleSubmit(mintAmount)}}>Mint</button>
     </>
      );
}

export default Mint;
