import React from 'react';
import '../assets/css/App.css';
import { motion } from "framer-motion"

const Reel = (props) => {
    
    //1 reel has 1 icon which is a vector of 3 images
    
    const reel= props.reel;

    return (
            <motion.img 
            animate={{
                scale: [1, 2, 2, 1, 1],
                rotate: [0, 0, 270, 270, 0],
                borderRadius: ["30%", "30%", "80%", "80%", "80%"]
              }}
              transition={{
                duration: 2,
                ease: "easeInOut",
                times: [0, 0.2, 0.5, 0.8, 1],
                repeat: Infinity,
                repeatDelay: 1
              }}
            src={require('../assets/images/symbols/'+props.reel+'.png')} alt=""/>
            )
    
    
}

export default Reel;
