import React, { useEffect, useRef, useState} from "react";
import Constants from "../Constants";
import "../assets/css/App.css";
import Reel from "./Reel";
import  Controller from './Controller.js'

const ReelSet = (props) => {
  const [gameOn, setGameOn] = useState(0);
  let zeroGame= 0;
  let refLights = useRef();

  let spinResultsIdx = [];
  let spinResults = [];
  let winningResults=[];
  let bet = 0;

const callbackCreateArray = (reelIdx, reel) => {
  spinResults.push(reel);
  spinResultsIdx.push(reelIdx);
}

const highLightWinningLines = () =>{
  for(let i=0; i<winningResults.length;i++){
    console.log("My winning reels are: "+Constants.REELS[i]);
  }
  //refLights.current.childNodes[0].children[0].children[0].children[0].attributes[2].nodeValue
}

/*
  console.log("My spinResultsIdx[] length is: "+spinResultsIdx.length)
  console.log("My winningResults length is: "+winningResults.length);
  for (let i = 0; i < winningResults.length; i++) {
    //this has been changed from Constants.LINES[i].length
    for (let j = 0; j < winningResults.length; j++) {
      let indexI = Constants.LINES[i][j][0];
      let indexJ = Constants.LINES[i][j][1];
      
      console.log("div= "+indexI);
      console.log("img= "+(spinResultsIdx[indexI])[indexJ]);
    }
  }
*/

const callbackWinningResults =(winningLines) =>{
  console.log("callbackWinningResults Setting winning lines to: "+winningLines);
  winningResults=winningLines;
  highLightWinningLines();
}

  const renderReels = () => {
    let reelList = Array.apply(null, Array(Constants.REELS)).map((el, idx) => {
      return <Reel reel={el} key={idx} index={idx} gameOn={gameOn} createArray={callbackCreateArray}/>;
    });
    
    return <>{reelList}</>;
  };  

 const calbackZeroGame = ()=>{
  zeroGame=false;
 } 

  const callBackSpinReel = () => {
    console.log("Spin Spin Spin");
      setGameOn(gameOn+1);  
  }; 

  return (
    <>
    <div id="jackpot" >
    Jackpot: <span>100</span>
  </div>
      <div className="container" >
        <div className="playContainer">
          <div className="reelSet"  ref={refLights}>
            {renderReels()}
          </div>
        </div>
      </div>
      <Controller spinReel={callBackSpinReel} zeroGame={calbackZeroGame} myResults={spinResults} myResultsIdx={spinResultsIdx} game={gameOn} winningLinesCallBack={callbackWinningResults}/>
      </>
  );
};

export default ReelSet;

/**
 * 
 * div =0 , spinResultsIdx[0]
My winning line is 0,0, 1,0, 2,0, 3,0, 4,0
My winning line is 0,1,1,1,2,1,3,1,4,1
