import React, { useRef, useState} from "react";
import Constants from "../Constants";
import Bet from "./Bet";
import Withdrawal from "./Withdrawal";
import "../assets/css/App.css";
import Reel from "./Reel";

const ReelSet = (props) => {
  //  const [spinReel, setSpinReel] = useState(false)
  const [gameOn, setGameOn] = useState(0);
  let [score, setScore] = useState(2);
  const [rolling, setRolling] = useState(false);
  let spinResults = [];
  let results = [];
  let bet = 0;
  let scr = 0;
  let winningLines = [];
  let reels = useRef();
  let countWin = 0;
  let symbolSellection = [];
  let randomNr = 0;
//  const symbols = "DDDGNNSSVVVCCFFRRYYIIJJZZMMOOLLTTPPBBQQAAG";
const symbols = "DDDGNNSS";

  const createArray = (randomNr) => {
    console.log("My random nr is" + -(randomNr / 242));
    for (let i = 0; i < 3; i++) {
      console.log("My sign is on position: "+(-(randomNr / 242) + i))
      results.push(symbols[-(randomNr / 242) + i]);
    }
  };

  const getRandomNr = () => {
    if (gameOn > 0) {
      randomNr = -(Math.floor(Math.random() * (symbols.length-3)) * 242);
    }
    console.log("randomNr = "+randomNr);
    createArray(randomNr);
    return randomNr;
  };

  const renderReels = () => {
    let reelList = Array.apply(null, Array(Constants.REELS)).map((el, idx) => {
      return <Reel reel={el} key={idx} index={idx} gameOn={getRandomNr()}/>;
    });

    return <>{reelList}</>;
  };

  const drawScore = (lineNumberType, fool) => {
    let newScore = 0;
    if (fool) {
      lineNumberType = 18;
    }
    switch (lineNumberType) {
      case 0: // top line
        //        audioWin.play();
        newScore = 0.5;
        break;
      case 1: // middle line
        //        audioWin.play();
        newScore = 0.5;
        break;
      case 2: // bottom line
        //        audioWin.play();
        newScore = 0.5;
        break;
      case 3: // V shape starting bottom left
        //        audioWin.play();
        newScore = 0.2;
        break;
      case 4: // V shape starting bottom right
        //        audioWin.play();
        newScore = 0.2;
        break;
      case 5: // W shape starting top left
        //        audioWin.play();
        newScore = 0.3;
        break;
      case 6: // M shape starting bottom left
        //        audioWin.play();
        newScore = 0.3;
        break;
      case 7: // M shape on top half
        //        audioWin.play();
        newScore = 0.3;
        break;
      case 8: // W shape on top half
        //        audioWin.play();
        newScore = 0.3;
        break;
      case 9: //W shape on bottom half
        //        audioWin.play();
        newScore = 0.3;
        break;
      case 10: // M shape on bottom half
        //        audioWin.play();
        newScore = 0.3;
        break;
      case 11: // U shape on top half
        //        audioWin.play();
        newScore = 0.1;
        break;
      case 12: // inverse U shape on top half
        //        audioWin.play();
        newScore = 0.1;
        break;
      case 13: // U shape on bottom half
        //        audioWin.play();
        newScore = 0.1;
        break;
      case 14: // inverse U shape on bottom half
        //        audioWin.play();
        newScore = 0.1;
        break;
      case 15: // inverse U shape on top half
        //        audioWin.play();
        newScore = 0.1;
        break;
      case 16: // Z shape from top left
        //        audioWin.play();
        newScore = 0.4;
        break;
      case 17: // Z shape from bottom left
        //        audioWin.play();
        newScore = 0.4;
        break;
      case 18: // Z shape from bottom left
        //        audioWinBonus.play();
        newScore = 100;
        break;
      default:
        newScore = 0;
    }

    console.log("My score is:" + newScore);
    setMyScore(newScore);
    
    return;
  };

  const createResults = () => {
    console.log("My results is: " + results);
    console.log("My results.length is: "+results.length)
    let myResults = [];
    let j=0;

    for(let i=0; i<=results.length;i++){
      
      myResults[j]=(results[i]);
      j++;

      if(j===3){
        spinResults.push(myResults);
        myResults=[];
        j=0;
      } 
    }
  };

  const spinReel = () => {
    setGameOn(gameOn+1);
    createResults();
    checkWin();
  // props.parrentCallBack(score);
  };

  const checkLinesElements = (symbol) => {
    countWin++;
    let symNr = 0;
    let fool = 0;
    for (let i = 0; i < Constants.LINES.length; i++) {
      fool = 0;
      symNr = 0;
      for (let j = 0; j < Constants.LINES[i].length; j++) {
        let indexI = Constants.LINES[i][j][0];
        let indexJ = Constants.LINES[i][j][1];

        if (spinResults[indexI][indexJ] === "D") {
          fool++;
        }
        if (symbol === spinResults[indexI][indexJ]) {
          symNr++;
        }
      }
      if (fool + symNr === 5 && fool < 4) {
        winningLines.push(i);
      }
    }
    winningLines = uniq(winningLines);
  };

  const uniq = (a) => {
    return a.sort().filter(function (item, pos, ary) {
      return !pos || item !== ary[pos - 1];
    });
  };

  const checkElements = () => {
    let fool = 0;
    for (let k = 0; k < 3; k++) {
      fool = 0;
      for (let i = 0; i < spinResults.length; i++) {
        let symb = "";
        for (let j = k; j < spinResults[i].length; j++) {
          if (spinResults[i][j] !== "D") {
            symb = spinResults[i][j];
            j = spinResults[i].length;
          } else {
            fool++;
            j = spinResults[i].length;
          }
        }
        if (fool < 4 && symb !== "") {
          symbolSellection.push(symb);
        }
      }
    }
    for (let i = 0; i < symbolSellection.length; i++) {
      checkLinesElements(symbolSellection[i]);
    }
  };

  const setBetZero = () => {
    bet = 0;
  };

  const setMyScore = (e) => {
    console.log("We are in setScore: " + e);
    let sc = 0;
    if (bet > 0 && e > 0) {
      console.log("Calculating:" + scr + "+(" + bet + "*" + e + ")");
      scr = scr + (e + bet * e);
      scr = Math.round(scr * 100) / 100;
    } else if (bet > 0 && e === 0) {
      scr = scr - (e + bet * e);
      console.log("Calculating:" + sc + "-(" + bet + "*" + e + ")");
      scr = Math.round(scr * 100) / 100;
    } else if (bet === 0 && e > 0) {
      console.log("Calculating: " + scr + " + " + e);
      scr = Math.round((scr + e) * 100) / 100;
    }
    setScore(scr);
  };
  const checkFourFools = () => {
    for (let i = 0; i < Constants.LINES.length; i++) {
      let fool = 0;
      for (let j = 0; j < Constants.LINES[0].length; j++) {
        let indexI = Constants.LINES[i][j][0];
        let indexJ = Constants.LINES[i][j][1];

        if (spinResults[indexI][indexJ] === "D") {
          fool++;
        }
      }
      if (fool === 4) {
        winningLines.push(i);
        drawScore(i, false);
      }
    }
  };

  const evaluateResults = () => {
    checkFourFools();
    checkElements();
    //highLightWinningLines(0);
    if (winningLines.length > 0) {
      console.log("We have winning line !!!");
      for (let i = 0; i < winningLines.length; i++) {
        drawScore(winningLines[i], false);
      }
    } else {
      console.log("We have no winning lines !!!");
      if (scr - bet >= 0.2) {
        scr = Math.round((scr - bet) * 100) / 100;
        setScore(scr);
        if (scr < bet) {
          setBetZero();
        }
      } else if (scr - bet === 0) {
        setBetZero();
        scr = 0;
        setScore(scr);
      }
    }
    //winningLines=[];
  };
  const checkFiveFools = () => {
    let fl = false;
    for (let i = 0; i < Constants.LINES.length; i++) {
      let fool = 0;
      for (let j = 0; j < Constants.LINES[0].length; j++) {
        let indexI = Constants.LINES[i][j][0];
        let indexJ = Constants.LINES[i][j][1];
        if (spinResults[indexI][indexJ] === "D") {
          fool++;
        }
      }
      console.log()
      if (fool === 5) {
        fl = true;
        winningLines.push(i);
          drawScore(i,true);
        //  highLightWinningLines(0);
        break;
      }
    }
    console.log("We have checked 5 fools, and returning: "+fl);
    return fl;
  };

  const checkWin = () => {
    if(gameOn>0){
    
    
    let fool = checkFiveFools();
    if (!fool) {
      evaluateResults();
    }
  }
  };

  return (
    <>
    <div id="jackpot" >
    Jackpot: <span>100</span>
  </div>
      <div className="container" >
        <div className="playContainer">
          <div className="reelSet" >
            {renderReels()} 
          </div>
        </div>
      </div>
            <div id="controls">
            <div
              className={!rolling ? "roll rolling" : "roll"}
              onClick={() => {
                spinReel();
              }}
              disabled={rolling} >
              {rolling ? "Rolling..." : "ROLL"}
            </div>
            <Withdrawal/>
            <Bet score={score} />
            <label>
              Score:<span id="score">{score}</span>
            </label>
          </div>
          </>
  );
};

export default ReelSet;
