import React, {useState} from 'react';
import Constants from '../Constants';
import Symbol from './Symbol';



const Reel = (props) =>{
    //42 symbols
    const symbols = "DDDGNNSSVVVCCFFRRYYIIJJZZMMOOLLTTPPBBQQAAG";
    const reelSymbols = symbols.repeat(Constants.REELS_REPEAT).split("");
    const reelEffect = 0;

    function getRandomInt() {
        return Math.floor(Math.random() * 42);
      }

    const renderImages = () =>{
     
        if(props.start){
            reelEffect=( -(getRandomInt()*242));
        }

        let imagesList = reelSymbols.map((el, idx)=>
            {return <Symbol symbol={el} key={idx} index={idx} spin={reelEffect} start={props.start}/>
         })
         return <>{imagesList}</>
        }

    return(
        
        <div className="reel" >
            {renderImages()}
        </div>
    )
}
export default Reel;
