import React, {useState} from 'react';
import Constants from '../Constants';
import Symbol from './Symbol';



const Reel = (props) =>{

    //42 symbols
    
    const reelSymbols = props.symb.repeat(Constants.REELS_REPEAT).split("");

 //   const [reelEffect , setReelEffect ] = useState(0)

    function getRandomInt() {
        return Math.floor(Math.random() * 42);
      }

//    if(props.reelSpin===1){
        console.log("My props.reelSpin is:"+props.reelSpin)
//   const setReelEffect(-(getRandomInt()*242));
//    }

const reelEffect = -(getRandomInt()*242);

    return(
        
        <div className="reel" >
            {
             reelSymbols.map((el, idx)=>
                {return <Symbol symbol={el} key={idx} index={idx} spin={reelEffect}/>
            })}
        </div>
    )
}
export default Reel;
