import React, { useState,useRef, useEffect} from "react";
import Constants from "../Constants";
import Symbol from "./Symbol";
import { motion } from "framer-motion";

let offset=0;

const Reel = (props) => {

  let symbols = Constants.REEL_SYMBOLS;
  let reelSymbols = "";
  let reels = [];
  let randomNr=0;
  let opacity=props.opacity;
  const[lightsOnOff,setLightsOnOff]=useState(false);
  let symbolRef = useRef();
  let reelRef = useRef([]);

  const randomBetween = (min, max) => {
    return Math.floor(Math.random() * (max - min + 1) + min);
  };

  const getRandomNr = () => {
    if (props.game > 0) {
    
    //  randomNr = -(Math.floor(Math.random() * ((symbols[props.index].length-3)+3)) * 246);
    randomNr = -(randomBetween(
        (Constants.REELS_REPEAT - 2) * symbols[props.index].length,
        (Constants.REELS_REPEAT - 8) * symbols[props.index].length
      ) * 246);
     
    }
    console.log("randomNr = "+randomNr);
    
    if(offset===randomNr){
        getRandomNr();
    }
   else{
        offset=randomNr;
    }
  };

  if (props.game === 0) {
    reelSymbols = symbols[props.index].split("");
  } else if(props.game>0){
    reelSymbols = symbols[props.index].repeat(Constants.REELS_REPEAT).split("");
    getRandomNr();
  }

  const lightsOff = () => {
    symbolRef.current.attributes[1].value="opacity: 0.3; transform: none;";
  }

  useEffect(() => {
    console.log("My ReelRef is: "+ symbolRef.current.attributes[1].value);
    lightsOff();
  },[lightsOnOff]);

  if(props.index===4){
    setLightsOnOff(true);
  }

  const createReels = () =>{
    reels=reelSymbols.map((el, idx) => {
        return (
          <Symbol symbol={el} key={idx} index={idx} spin={props.game} />
        );
      })
      return <>{reels}</>
  }


  const variants = {
    initial:{
        opacity: 1 
    },
    spin: {
       y:offset,
       opacity:opacity, 
       transition:{ 
            duration: 3, 
            delay: 
            props.index * 0.8 
        },
    },
    exit : { opacity: 1 }
  };
 
  return (
    <div className="reel" >
      <motion.div variants={variants}  initial="initial"  animate="spin" className="icons" ref={symbolRef}>
        {createReels()}
      </motion.div>
    </div>
  );
};
export default Reel;

