import  React, { useEffect, useState, useRef } from "react";
import '../assets/css/App.css';
import {ethers} from "ethers";
import PFPRacerNFT from '../artifacts/contracts/PFPRacerNFT.sol/PFPRacerNFT.json'
 
const Withdrawal=(props)=>{
    const [rolling, setRolling] = useState(false);
    const [name, setName] = useState("");
    const [account, setAccount] = useState("");
    const [contract, setContract] = useState("");
    let refCode ="";
    const initConnection = async () =>{
      if(typeof window.ethereum !== "undefined"){
        const accounts = await window.ethereum.request({
          method: "eth_requestAccounts",
        });
        const provider = new ethers.providers.Web3Provider(window.ethereum);
        const newSigner = provider.getSigner();
        setAccount(accounts[0]);
        setContract(
          new ethers.Contract(
            "0xE0059D8C4242BDf03c9ca37E0e651c07b1b9d587",
            PFPRacerNFT.abi,
            newSigner
          )
        )
      }else{
        console.log("Please install metamask");
      }
    }

    const getNFTBalance = async () =>{
      let balance = await contract.getNFTBalance();
      console.log("How many NFT's have been  minted ? "+balance );

      return balance;
    }

    const howManyNFTsAreLeft = async () =>{
      let totalNFT = await contract.maxSupply();
      let mintedNFT= await getNFTBalance();
      let nftLeftToMint = totalNFT -mintedNFT;

      console.log("Total NFT's left to mint "+nftLeftToMint);
    }

    useEffect(()=>{
      initConnection();
    },[account]);

  const makeWithdrawal = async(refCode) => {
    let score = Math.floor(props.getScoreCallback());
    console.log("My score in makeWithdrawal is "+score);
     if(score > 0){
    window.confirm("Please be aware: if you have 1.2 or 20.2 NFT's you will be able to withdrawal only the integer part like 1 or 2.");
    //await contract.withdrawal(refCode);
    console.log("This is the withdrawal function !!!");
    await contract.withdrawal(score,refCode);
    let myScr= props.getScoreCallback()-score;
    console.log("My score is "+myScr);
    //props.callbackSetScore(myScr);
    props.setScoreCallBack(myScr);
    }
  }
    
  const withdrawal = () => {
    console.log("My withdrawal is: "+props.blockWithdraw);
    if(!props.blockWithdraw){
    let score = Math.floor(props.getScoreCallback());
        console.log("Withdrawal has been activated");
        console.log("My account is: "+account);
        howManyNFTsAreLeft();
  if(score>0){
    refCode = prompt("Please enter your refCode:", "RefCode");
    if (refCode == null || refCode == "" || refCode !== "RACECOUPLE2022") {
      let text = "Withdrawal has been canceled !!! You have the option to buy the NFT's";
      window.confirm(text)
    } else {
        makeWithdrawal(refCode);
      }
    
  }else{
    window.confirm("You need to have at least 1 NFT to withdrawal");
  }
} 
};

    return(
    <div
         className={withdrawal ? "roll rolling" : "roll"}
         onClick={() => {withdrawal()}}
    >Withdrawal</div>
    )
}

export default Withdrawal;
