import React, { useState, useRef, useEffect } from "react";
import Constants from "../Constants";
import "../assets/css/App.css";
import Reel from "./Reel";

const ReelSet = (props) => {
  //  const [spinReel, setSpinReel] = useState(false)
  const spinResults = [];
  const winningLines = [];
  let reels= useRef([]);

  const getRandomNr = () => {
    let randomNr = 0;
    if (props.startGame > 0) {
      randomNr = -(Math.floor(Math.random() * 42) * 242);
    }
    return randomNr;
  };
  const renderReels = () => {
    let reelList = Array.apply(null, Array(Constants.REELS)).map((el, idx) => {
      return <Reel reel={el} key={idx} index={idx} gameOn={getRandomNr()} rls={reels[idx]=el}/>;
    });
    return <>{reelList}</>;
  };

  return (
    <>
      <div className="container">
        <div className="playContainer">
          <div className="reelSet">{renderReels()}</div>
        </div>
      </div>
    </>
  );
};

export default ReelSet;
