    console.log("Receiving from ReelSet myResultsIdx: "+props.myResultsIdx);

    const setBetCallBack=(myBet)=>{
        bet=myBet;
    }

    const setScoreCallBack = (myScore) =>{
        score=myScore;
    }
    
    const callbackSpin =()=>{
        if(bet===0){
        }else{
            props.spinReel();
        }
    }

    return(
        <div id="controls">
        <div
          className={!rolling ? "roll rolling" : "roll"}
          onClick={() => {
            callbackSpin();
          }}
          disabled={rolling} >
          {rolling ? "Rolling..." : "ROLL"}
        </div>
        <Withdrawal/>
        <ScoreBet callBackSetBet={setBetCallBack} callbackSetScore={setScoreCallBack}/>
          
      </div>
    )
}

export default Controller;
