import React, { useEffect, useRef, forwardRef} from "react";
import Image from "../assets/images/Images";
import {motion,animate,Variants, AnimatePresence} from "framer-motion";

const Symbol = forwardRef ((props, ref)=> {
  let image = "";
  let imageRef=  useRef();
 
  const getImage =  () => {
    if(props.spin<0){
    switch (props.symbol) {
      
      case "A":
        return Image.America;
      case "G":
        return Image.Aragon;
      case "N":
        return Image.Argentina;
      case "S":
        return Image.Assen;
      case "U":
        return Image.Australia;
      case "C":
        return Image.Catalunya;
      case "F":
        return Image.Finland;
      case "R":
        return Image.France;
      case "Y":
        return Image.Germany;
      case "I":
        return Image.Indonesia;
      case "J":
        return Image.Japan;
      case "Z":
        return Image.Jerez;
      case "M":
        return Image.Malaysia;
      case "O":
        return Image.Misano;
      case "L":
        return Image.Mugello;
      case "T":
        return Image.Osterreich;
      case "P":
        return Image.Portugal;
      case "Q":
        return Image.Qatar;
      case "H":
        return Image.Thailand;
      case "K":
        return Image.UK;
      case "D":
        return Image.Fool;
      case "V":
        return Image.Valencia;
      default:
        return Image.Fool;
    }
  }else{
    return Image.Fool;
  }
  };

    image = getImage();

    const variants =  {
      hidden: {opacity: 0},
      visible:() =>({
        opacity:1,
        transition: {
          delay: 0.1*Math.floor(props.index/3)
        }
      }),
      exist:{opacity:0.3}
    }

    const spinReel =()=>{
      return (
        
        <div className="icons" >
          <AnimatePresence>
          <motion.img key={props.index}          
                      animate={{ y: props.spin, opacity: props.opacity }}
                      transition={{
                        type: 'tween',
                        duration: 1.5,
                        ease: 'easeInOut'
                      }} 
                      variants={variants}
                      initial="hidden"
                      
                    src={image} alt=""/>
          </AnimatePresence>
                  </div>
                 
      );
  }

    return (
      <>{spinReel()}</>
    );

});

export default Symbol;

   //   variants={container} initial="hidden" animate="visible"
