            'Accept': 'application/json',
            'Content-Type': 'application/json',
          },
          body: dataToSend
        })
    
      }

const handleSubmit= async() =>{
  let transaction = "";
  let errorTx= false;
    if(mintAmount>0&&mintAmount<1001){
        try{
            const{ethereum}=window;

            if(ethereum){
   
                let cost = await props.contract.cost();
