        </label>
          <label className="form-label">
            Mint Amount:
            <input
              type="text"
              value={mintAmount}
              onChange={e => setMintAmount(e.target.value)}
            />
          </label>
          <label className="label-mint">
            Price per/NFT is 10 Matic
            </label>
        </form>
        </div>
     <button className="form-submit-button" onClick={()=>{handleSubmit(mintAmount)}}>Mint</button>
     </>
      );
}

