import React, { useRef,useEffect, useState } from "react";
import {ethers} from "ethers";
import ReelSet from "./components/ReelSet";
import "./assets/css/App.css";
import { assert } from "chai";
import GameScoreCarousels from './components/GameScoreCarousel';
import WelcomeMessage from './components/WelcomeMessage';

import axios from 'axios';

const App = () => {
  const [spin, setSpin] = useState(0);
  const [showList, setShowList] = useState(0);
  const [widthCarousel, setWidthCarousel] = useState(0);
  const carousel = useRef();

  const callbackSetSpin = (spin) => {
    setSpin(spin);
  };

  const callBackShowWin = () => {
    setShowList(1);
  };

  return (
    <div className="game">
      <WelcomeMessage />
      <ReelSet
        runGame={spin}
        spinCallback={callbackSetSpin}
        showWinCallback={callBackShowWin}
      />
      <GameScoreCarousels/>
    </div>

  );
};
export default App;
